mapper.select:
select * from ( select rownum as RNUM, A.* from ( SELECT r.*,p.pay_condition,(select count(*) from tbl_review v where v.RESERVATION_SEQ=r.reservation_seq)V FROM tbl_reservation r join tbl_payment p on r.RESERVATION_SEQ = p.RESERVATION_SEQ LEFT join tbl_refund f on p.pay_seq=f.pay_seq where p.PAY_CONDITION='Y' and f.refund_cancle is null and r.select_date+1<sysdate and res_date>=#{startDay} and res_date < to_Date(#{endDay})+1
and userid=#{customerId}
order by r.reservation_seq desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update tbl_user
set mileagePoint=#{reviewMileage}
where userid=#{userid}

mapper.select:
select *
from
	(
	    select rownum as RNUM, A.*
	    from
	    (
	        select r.*,p.prfnm,p.perfomtype,p.pay_ticket_number
			from tbl_review r join tbl_reservation p
			on r.RESERVATION_SEQ=p.RESERVATION_SEQ
			where review_regdate>=#{startDay}
			 and review_regdate < to_Date(#{endDay})+1
			
				and r.userid=#{customerId}
			
			order by review_seq desc
	    )A
	)
	where RNUM > #{firstRecordIndex} 

and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

select count(*)
from tbl_review r join tbl_reservation p
on r.RESERVATION_SEQ=p.RESERVATION_SEQ
where review_regdate>=#{startDay}
 and review_regdate < to_Date(#{endDay})+1

	and r.userid=#{customerId}
